// COMPONENTS
import React, { useEffect, useState } from 'react';
import { useUsers } from '../../Context/Users';
import { useLoading } from '../../Context/Carregando';
import axios from 'axios';
import Top from '../../components/Header/Top';
import MainUser from '../../components/MainUser';
import Footer from '../../components/Footer';

// CSS
import './index.css';

// RENDERIZA A PÁGINA USERS
const Usuario = (props) => {
  const { users, setUsers } = useUsers();
  const { carregando, setCarregando } = useLoading();
  const [loading, setLoading] = useState(null);

  // PUXA OS DADOS DA API COM O USUÁRIO DIGITADO NO INPUT
  useEffect(() => {
    setLoading(true);
    getUserGitHub(users.login);
  }, [setUsers]);

  // FUNÇÃO PARA PUXAR DADOS DA API GITHUB ( NA REFATORAÇÃO UTILIZAR ASYNC , AWAIT )
  function getUserGitHub(user) {
    setLoading(true);
    axios.get(`https://api.github.com/users/${user}`).then((response) => {
      axios
        .get(`https://api.github.com/users/${user}/repos`)
        .then((repositorys) => {
          const {
            id,
            login,
            avatar_url,
            html_url,
            repos_url,
            name,
            company,
            blog,
            location,
            bio,
          } = response.data;
          setLoading(false);
          setUsers({
            id,
            login,
            avatar_url,
            html_url,
            repos_url,
            name,
            company,
            blog,
            location,
            bio,
            repos: repositorys.data,
          });
        });
    });
  }

  const Carregando = () => {
    return <h1>Carregando...</h1>;
  };

  return (
    <>
      <div className="main-container">
        <header className="header-user">
          <Top />
        </header>
        <div className="line-user"></div>
        {loading && <Carregando />}
        {!loading && users.id && <MainUser />}
      </div>
      <Footer />
    </>
  );
};

export default Usuario;
