import React, { useState } from 'react';
import { useUsers } from '../../../../Context/Users';
import axios from 'axios';

// CSS
import './index.css';

export default (props) => {
  const { setUsers } = useUsers();
  const [pesquisa, setPesquisa] = useState();
  const [loading, setLoading] = useState(null);

  // FUNÇÃO PARA PUXAR DADOS DA API GITHUB ( NA REFATORAÇÃO UTILIZAR ASYNC , AWAIT )
  function getUserGitHub(user) {
    setLoading(true);
    axios.get(`https://api.github.com/users/${user}`).then((response) => {
      axios
        .get(`https://api.github.com/users/${user}/repos`)
        .then((repositorys) => {
          const {
            id,
            login,
            avatar_url,
            html_url,
            repos_url,
            name,
            company,
            blog,
            location,
            bio,
          } = response.data;
          setUsers({
            id,
            login,
            avatar_url,
            html_url,
            repos_url,
            name,
            company,
            blog,
            location,
            bio,
            repos: repositorys.data,
          });
        });
    });
    setLoading(false);
  }

  // INSERE O VALOR DO INPUT NO ESTADO DE PESQUISA
  function changeValue(e) {
    const searchUser = e.target.value;
    setPesquisa(searchUser);
  }

  // INSERE O VALOR DO INPUT NO ESTADO DE USERS ( USERS: RECEBE OS LOGIN "USER" PARA REQUISIÇÃO API )
  function handleSubmit(search) {
    setUsers({ login: search });
    getUserGitHub(search);
  }

  return (
    <>
      <input
        className="search-user"
        type="text"
        placeholder={props.placeholder}
        onChange={changeValue}
      />
      <button className="orange" onClick={() => handleSubmit(pesquisa)}>
        Pesquisar
      </button>
    </>
  );
};
